import React, { createContext, useState, useEffect } from 'react';
import { jwtDecode } from 'jwt-decode';  // Corregir la importación

export const AuthContext = createContext();

export const AuthProvider = ({ children }) => {
  const [auth, setAuth] = useState({ user: null, roles: [] });

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (token) {
      const decoded = jwtDecode(token);  // Usar jwtDecode correctamente
      console.log(decoded);  // Agregar un console.log para ver si los roles están bien en el token
      setAuth({ user: decoded.id, roles: decoded.roles || [] });  // Asegurarse de que 'roles' esté presente
    }
  }, []);

  const login = (token) => {
    const decoded = jwtDecode(token);  // Usar jwtDecode correctamente
    console.log(decoded);  // Verificar que los roles estén en la respuesta decodificada
    localStorage.setItem('token', token);
    setAuth({ user: decoded.id, roles: decoded.roles || [] });  // Asegurarse de que 'roles' esté presente
  };

  const logout = () => {
    localStorage.removeItem('token');
    setAuth({ user: null, roles: [] });
  };

  return (
    <AuthContext.Provider value={{ auth, login, logout }}>
      {children}
    </AuthContext.Provider>
  );
};
