import React, { useContext } from 'react';
import { Link } from 'react-router-dom';
import { AuthContext } from '../auth-context';

const Navbar = () => {
  const { auth, logout } = useContext(AuthContext);

  // Comprobamos que 'auth' y 'auth.roles' existen antes de usar 'includes'
  const roles = auth && auth.roles ? auth.roles : [];

  return (
    <nav>
      <Link to="/">Home</Link>
      {auth && auth.user ? (
        <>
          <Link to="/profile">Profile</Link>
          {roles.includes('admin') && <Link to="/admin">Admin Board</Link>}
          {roles.includes('moderator') && <Link to="/mod">Moderator Board</Link>}
          <button onClick={logout}>Logout</button>
        </>
      ) : (
        <>
          <Link to="/login">Login</Link>
          <Link to="/register">Register</Link>
        </>
      )}
    </nav>
  );
};

export default Navbar;
