import React, { useContext } from 'react';
import { AuthContext } from '../auth-context';

const Profile = () => {
  const { auth } = useContext(AuthContext);

  return (
    <div className="min-h-screen bg-gray-100 flex items-center justify-center p-4">
      <div className="bg-white shadow-md rounded-lg p-8 max-w-xl w-full">
        <h2 className="text-2xl font-bold mb-4 text-gray-800">Perfil del Usuario</h2>
        <div className="space-y-2">
          <p><strong>Usuario:</strong> {auth.user}</p>
          <p><strong>Roles:</strong> {auth.roles.length > 0 ? auth.roles.join(', ') : 'Ninguno'}</p>
        </div>
      </div>
    </div>
  );
};

export default Profile;
