import React, { useContext } from 'react';
import { Link } from 'react-router-dom';
import { AuthContext } from '../auth-context';

const Navbar = () => {
  const { auth, logout } = useContext(AuthContext);

  return (
    <nav>
      <Link to="/">Home</Link>
      {auth.user ? (
        <>
          <Link to="/profile">Profile</Link>
          {/* Usar el operador de encadenamiento opcional para evitar errores si auth.roles es undefined */}
          {auth.roles?.includes('admin') && <Link to="/admin">Admin Board</Link>}
          {auth.roles?.includes('moderator') && <Link to="/mod">Moderator Board</Link>}
          <button onClick={logout}>Logout</button>
        </>
      ) : (
        <>
          <Link to="/login">Login</Link>
          <Link to="/register">Register</Link>
        </>
      )}
    </nav>
  );
};

export default Navbar;
