import React, { useContext } from 'react';
import { AuthContext } from '../auth-context';

const Profile = () => {
  const { auth } = useContext(AuthContext);

  return (
    <div>
      <h2>Profile</h2>
      {/* Asegúrate de que auth.user esté definido */}
      <p>User ID: {auth.user}</p>
      {/* Usar el operador de encadenamiento opcional para evitar el error de 'join' si auth.roles es undefined */}
      <p>Roles: {auth.roles?.join(', ') || 'No roles assigned'}</p>
    </div>
  );
};

export default Profile;
