import React, { createContext, useState, useEffect } from 'react';
import { jwtDecode } from 'jwt-decode';  // ðŸ‘ˆ ImportaciÃ³n corregida

export const AuthContext = createContext();

export const AuthProvider = ({ children }) => {
  const [auth, setAuth] = useState({ user: null, roles: [] });

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (token) {
      const decoded = jwtDecode(token);  // ðŸ‘ˆ Uso correcto
      setAuth({ user: decoded.id, roles: decoded.roles });
    }
  }, []);

  const login = (token) => {
    const decoded = jwtDecode(token);  // ðŸ‘ˆ Uso correcto
    localStorage.setItem('token', token);
    setAuth({ user: decoded.id, roles: decoded.roles });
  };

  const logout = () => {
    localStorage.removeItem('token');
    setAuth({ user: null, roles: [] });
  };

  return (
    <AuthContext.Provider value={{ auth, login, logout }}>
      {children}
    </AuthContext.Provider>
  );
};
