import React, { useContext } from 'react';
import { Formik, Form, Field, ErrorMessage } from 'formik';
import * as Yup from 'yup';
import axios from 'axios';
import { AuthContext } from '../auth-context';
import { useNavigate } from 'react-router-dom';

const Login = () => {
  const { login } = useContext(AuthContext);
  const navigate = useNavigate();

  const initialValues = { username: '', password: '' };

  const validationSchema = Yup.object({
    username: Yup.string().required('Campo requerido'),
    password: Yup.string().required('Campo requerido'),
  });

  const onSubmit = (values, { setSubmitting }) => {
    axios.post('http://localhost:3000/api/auth/signin', values)
      .then(res => {
        login(res.data.accessToken);
        navigate('/profile');
      })
      .catch(() => alert('Credenciales incorrectas o error de red.'))
      .finally(() => setSubmitting(false));
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-gradient-to-br from-indigo-500 to-purple-600 p-4">
      <div className="bg-white p-8 rounded-lg shadow-lg w-full max-w-md">
        <h2 className="text-2xl font-bold mb-6 text-center text-indigo-700">Iniciar Sesión</h2>
        <Formik initialValues={initialValues} validationSchema={validationSchema} onSubmit={onSubmit}>
          <Form className="space-y-4">
            <div>
              <label className="block font-semibold">Usuario</label>
              <Field name="username" type="text" className="w-full px-3 py-2 border rounded shadow-sm" />
              <ErrorMessage name="username" component="div" className="text-red-500 text-sm" />
            </div>
            <div>
              <label className="block font-semibold">Contraseña</label>
              <Field name="password" type="password" className="w-full px-3 py-2 border rounded shadow-sm" />
              <ErrorMessage name="password" component="div" className="text-red-500 text-sm" />
            </div>
            <button type="submit" className="w-full bg-indigo-600 hover:bg-indigo-700 text-white py-2 rounded">
              Ingresar
            </button>
          </Form>
        </Formik>
      </div>
    </div>
  );
};

export default Login;
