import db from "../models/index.js";
import jwt from "jsonwebtoken";
import bcrypt from "bcryptjs";

const User = db.user;
const Role = db.role;

export const signup = async (req, res) => {
  try {
    // Crear nuevo usuario
    const user = await User.create({
      username: req.body.username,
      email: req.body.email,
      password: bcrypt.hashSync(req.body.password, 8),
    });

    // Si vienen roles en el request
    if (req.body.roles) {
      const roles = await Role.findAll({
        where: {
          name: req.body.roles,
        },
      });
      await user.setRoles(roles); // asociaci贸n muchos a muchos
    } else {
      // Rol por defecto: user
      const role = await Role.findOne({ where: { name: "user" } });
      await user.setRoles([role]);
    }

    res.send({ message: "User registered successfully!" });

  } catch (err) {
    res.status(500).send({ message: err.message });
  }
};

export const signin = async (req, res) => {
  try {
    const user = await User.findOne({
      where: {
        username: req.body.username,
      },
      include: [
        {
          model: Role,
          as: "roles", // Este alias debe estar en tu asociaci贸n
          attributes: ["name"],
          through: { attributes: [] }, // no queremos info extra de user_roles
        },
      ],
    });

    if (!user) {
      return res.status(404).send({ message: "User Not Found." });
    }

    const passwordIsValid = bcrypt.compareSync(
      req.body.password,
      user.password
    );

    if (!passwordIsValid) {
      return res.status(401).send({
        accessToken: null,
        message: "Invalid Password!",
      });
    }

    // Obtener nombres de roles
    const authorities = user.roles.map((role) => role.name);

    // Generar token con id y roles
    const token = jwt.sign(
      {
        id: user.id,
        username: user.username,
        roles: authorities, //  Aseguramos que el token tenga los roles
      },
      process.env.JWT_SECRET || "jwt-secret-key",
      {
        expiresIn: 86400, // 24 horas
      }
    );

    res.status(200).send({
      id: user.id,
      username: user.username,
      email: user.email,
      roles: authorities,
      accessToken: token,
    });

  } catch (err) {
    res.status(500).send({ message: err.message });
  }
};
