import React from 'react';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom'; // Cambié Switch por Routes
import { AuthProvider } from './auth-context';
import Navbar from './components/Navbar';
import Login from './pages/Login';
import Register from './pages/Register';
import Profile from './pages/Profile';
import AdminBoard from './pages/AdminBoard';
import ModeratorBoard from './pages/ModeratorBoard';
import UserBoard from './pages/UserBoard';
import ProtectedRoute from './components/ProtectedRoute';

function App() {
  return (
    <AuthProvider>
      <Router>
        <Navbar />
        <Routes> {/* Cambié Switch por Routes */}
          <Route path="/" element={<UserBoard />} /> {/* Cambié component={UserBoard} por element={<UserBoard />} */}
          <Route path="/login" element={<Login />} /> {/* Cambié component={Login} por element={<Login />} */}
          <Route path="/register" element={<Register />} /> {/* Cambié component={Register} por element={<Register />} */}
          <Route path="/profile" element={<ProtectedRoute component={Profile} />} /> {/* Protegido con ProtectedRoute */}
          <Route path="/admin" element={<ProtectedRoute roles={['admin']} component={AdminBoard} />} /> {/* Protegido con roles */}
          <Route path="/mod" element={<ProtectedRoute roles={['moderator']} component={ModeratorBoard} />} /> {/* Protegido con roles */}
        </Routes>
      </Router>
    </AuthProvider>
  );
}

export default App;
