import React from 'react';
import { Formik, Form, Field, ErrorMessage } from 'formik';
import * as Yup from 'yup';
import axios from 'axios';

const Register = () => {
  const initialValues = { username: '', email: '', password: '', roles: [] };

  const validationSchema = Yup.object({
    username: Yup.string().required('Campo requerido'),
    email: Yup.string().email('Correo inválido').required('Campo requerido'),
    password: Yup.string().min(6, 'Mínimo 6 caracteres').required('Campo requerido'),
  });

  const onSubmit = (values, { setSubmitting }) => {
    axios.post('http://localhost:3000/api/auth/signup', values)
      .then(() => alert('Usuario registrado correctamente'))
      .catch(() => alert('Error al registrar usuario'))
      .finally(() => setSubmitting(false));
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-gradient-to-r from-green-400 to-blue-500 p-4">
      <div className="bg-white p-8 rounded-lg shadow-lg w-full max-w-md">
        <h2 className="text-2xl font-bold mb-6 text-center text-green-700">Registrarse</h2>
        <Formik initialValues={initialValues} validationSchema={validationSchema} onSubmit={onSubmit}>
          <Form className="space-y-4">
            <div>
              <label className="block font-semibold">Usuario</label>
              <Field name="username" className="w-full px-3 py-2 border rounded shadow-sm" />
              <ErrorMessage name="username" component="div" className="text-red-500 text-sm" />
            </div>
            <div>
              <label className="block font-semibold">Correo</label>
              <Field name="email" type="email" className="w-full px-3 py-2 border rounded shadow-sm" />
              <ErrorMessage name="email" component="div" className="text-red-500 text-sm" />
            </div>
            <div>
              <label className="block font-semibold">Contraseña</label>
              <Field name="password" type="password" className="w-full px-3 py-2 border rounded shadow-sm" />
              <ErrorMessage name="password" component="div" className="text-red-500 text-sm" />
            </div>
            <div>
              <label className="block font-semibold">Roles</label>
              <Field name="roles" as="select" multiple className="w-full px-3 py-2 border rounded shadow-sm">
                <option value="user">User</option>
                <option value="moderator">Moderator</option>
                <option value="admin">Admin</option>
              </Field>
            </div>
            <button type="submit" className="w-full bg-green-600 hover:bg-green-700 text-white py-2 rounded">
              Registrarse
            </button>
          </Form>
        </Formik>
      </div>
    </div>
  );
};

export default Register;
